pi(3.).

drawTree(_X1, _Y1, _Angle, 0, []).
drawTree(X1, Y1, Angle, Depth, Lista) :-
    pi(P),
    X2 is X1 + cos(Angle * P / 180.0) * Depth * 10.0,
    Y2 is Y1 + sin(Angle * P / 180.0) * Depth * 10.0,
    A1 is Angle - 30,
    A2 is Angle + 30,
    De is Depth - 1,
    drawTree(X2, Y2, A1, De, L1),
    drawTree(X2, Y2, A2, De, L2),
    append([[X1, Y1, X2, Y2]], L1, Concat1),
    append(Concat1, L2, Lista), !.

fractal(V, L) :-
	drawTree(400, 500, -90, V, L), !.